void drawdmap(BITMAP *dest, int32_t x, int32_t y, bool showmap, int32_t showhero, int32_t showcompass, int32_t herocolor, int32_t lccolor, int32_t dccolor)
{
    zcolors c=QMisc.colors;
    int32_t type = (DMaps[get_currdmap()].type&dmfTYPE);
    
    if(showmap)
    {
        switch(type)
        {
        case dmOVERW:
        case dmBSOVERW:
		{
            int32_t maptile=(!get_qr(qr_BROKEN_OVERWORLD_MINIMAP) && has_item(itype_map, get_dlevel()))?DMaps[get_currdmap()].minimap_2_tile:DMaps[get_currdmap()].minimap_1_tile;
            int32_t mapcset=(!get_qr(qr_BROKEN_OVERWORLD_MINIMAP) && has_item(itype_map, get_dlevel()))?DMaps[get_currdmap()].minimap_2_cset:DMaps[get_currdmap()].minimap_1_cset;
            //What a mess. The map drawing is based on a variable that can change states during a scrolling transition when warping. -Z
            if(maptile)
            {
                draw_block(dest,x,y,maptile,mapcset,5,3);
            }
            else if(c.overworld_map_tile || c.overworld_map_tile)
            {
                draw_block(dest,x,y,(c.overworld_map_tile!=0?c.overworld_map_tile:c.overworld_map_tile),c.overworld_map_cset,5,3);
            }
            else
            {
                rectfill(dest,x+8,y+8,x+71,y+39,c.overw_bg);
            }
            
            if(!DMaps[get_currdmap()].minimap_1_tile && ((DMaps[get_currdmap()].type&dmfTYPE) == dmBSOVERW))
            {
                drawgrid(dest,x+8,y+8,c.bs_goal,c.bs_dk);
            }
            
            break;
        }
        case dmDNGN:
        case dmCAVE:
		{
            int32_t maptile=has_item(itype_map, get_dlevel())?DMaps[get_currdmap()].minimap_2_tile:DMaps[get_currdmap()].minimap_1_tile;
            int32_t mapcset=has_item(itype_map, get_dlevel())?DMaps[get_currdmap()].minimap_2_cset:DMaps[get_currdmap()].minimap_1_cset;
            //What a mess. The map drawing is based on a variable that can change states during a scrolling transition when warping. -Z
            if(maptile)
            {
                draw_block(dest,x,y,maptile,mapcset,5,3);
            }
            else if(c.dungeon_map_tile||c.dungeon_map_tile)
            {
                draw_block(dest,x,y,(c.dungeon_map_tile!=0?c.dungeon_map_tile:c.dungeon_map_tile),c.dungeon_map_cset,5,3);
            }
            else
            {
                rectfill(dest,x+8,y+8,x+71,y+39,c.dngn_bg);
            }
            //Marking this as a possible area for the scrolling warp map bug reported by Lut. -Z
            if(!DMaps[get_currdmap()].minimap_2_tile && has_item(itype_map, get_dlevel()))
            {
                if((DMaps[get_currdmap()].flags&dmfMINIMAPCOLORFIX) != 0)
                {
                    drawgrid(dest,x+8,y+8,c.cave_fg,-1);
                }
                else
                {
                    drawgrid(dest,x+8,y+8,c.dngn_fg,-1);
                }
            }
            
            break;
		}
        }
    }
    
    if(showcompass)
    {
        if(type==dmDNGN || type==dmCAVE)
        {
            if(show_subscreen_dmap_dots&&has_item(itype_compass, get_dlevel()))
            {
                int32_t c2 = dccolor;
                
                if(!has_item(itype_triforcepiece, get_dlevel()) && (frame&16))
                    c2 = lccolor;
                    
                int32_t cx = ((DMaps[get_currdmap()].compass&15)<<3)+x+10;
                int32_t cy = ((DMaps[get_currdmap()].compass&0xF0)>>2)+y+8;
                putdot(dest,cx,cy,c2);
            }
        }
    }
    
    if(showhero)
    {
        if(show_subscreen_dmap_dots && herocolor != 255)
        {
            if(type==dmOVERW)
            {
                putdot(dest,((get_homescr()&15)<<2)+x+9,((get_homescr()&0xF0)>>2)+y+8,herocolor);
            }
            else if(type==dmBSOVERW || ((type==dmDNGN || type==dmCAVE) && get_currscr()<128))
            {
                putdot(dest,(((get_homescr()&15)-DMaps[get_currdmap()].xoff)<<3)+x+10,((get_homescr()&0xF0)>>2)+y+8,herocolor);
            }
        }
    }
}

void lifemeter(BITMAP *dest,int32_t x,int32_t y,int32_t cs,bool bs_style)
{
	if(!show_subscreen_life)
	{
		return;
	}
	
	if(!bs_style)
	{
		y+=24;
	}
	int32_t tile = 0;
	const int32_t basetile = wpnsbuf[iwQuarterHearts].tile;
	const int32_t max_iter = (game != NULL ? zc_min(game->get_maxlife(),game->get_hp_per_heart()*24) : 1);
	const int32_t inc = (game != NULL ? game->get_hp_per_heart() : 16);
	
	for(int32_t i=0; i<max_iter; i+=inc)
	{
		if(game != NULL)
		{
			if(get_qr(qr_QUARTERHEART))
			{
				if(i+((game->get_hp_per_heart()/4)*3)>=game->get_life()) tile= (basetile*4)+2;
				
				if(i+(game->get_hp_per_heart()/2)>=game->get_life()) tile=1;
				
				if(i+((game->get_hp_per_heart()/4)*1)>=game->get_life()) tile= (basetile*4)+3;
			}
			else if(i+(game->get_hp_per_heart()/2)>=game->get_life()) tile=1;
			
			if(i>=game->get_life()) tile=4;
		}
		else
			tile=4;
		
		overtile8(dest,tile,x,y,cs,0);
		x+=8;
		
		if(((i>>4)&7)==7)
		{
			x-=64;
			y+=bs_style?8:-8;
		}
	}
}

void magicgauge(BITMAP *dest,int32_t x,int32_t y, int32_t container, int32_t notlast_tile, int32_t notlast_cset, bool notlast_mod, int32_t last_tile, int32_t last_cset, bool last_mod,
                int32_t cap_tile, int32_t cap_cset, bool cap_mod, int32_t aftercap_tile, int32_t aftercap_cset, bool aftercap_mod, int32_t frames, int32_t speed, int32_t delay, bool unique_last, int32_t show)
{
    //these are here to bypass compiler warnings about unused arguments
    frames=frames;
    speed=speed;
    delay=delay;
    
    // show
    //   <0: always
    //  >=0: when drain rate matches show
    if(show > -1)
    {
		if(show != game->get_magicdrainrate())
            return;
    }
    
    int32_t containers=game->get_maxmagic()/game->get_mp_per_block();
    int32_t tile=zc_oldrand()%32767, cset=zc_oldrand()%15;
    bool mod_value=(zc_oldrand()%2)!=0;
    
    if(container<containers)
    {
        tile=notlast_tile;
        cset=notlast_cset;
        mod_value=notlast_mod;
    }
    else if(container==containers)
    {
        tile=last_tile;
        cset=last_cset;
        mod_value=last_mod;
    }
    else if(container==containers+1)
    {
        tile=cap_tile;
        cset=cap_cset;
        mod_value=cap_mod;
    }
    else //if (container>containers+1)
    {
        tile=aftercap_tile;
        cset=aftercap_cset;
        mod_value=aftercap_mod;
    }
    
    if(mod_value)
    {
        if(game->get_magic()>=container*game->get_mp_per_block())
        {
            //tile=tile;                                        //full block
            if(game->get_magic()==container*game->get_mp_per_block())
            {
                if(unique_last)
                {
                    tile+=game->get_mp_per_block()+3;
                }
            }
        }
        else
        {
            if(((container-1)*game->get_mp_per_block())>game->get_magic())
            {
                tile+=4;                                //empty block
            }
            else
            {
                tile+=4+((game->get_magic()-((container-1)*game->get_mp_per_block()))%game->get_mp_per_block());
            }
        }
    }
    
    //tile=14161*4;
    overtile8(dest,tile,x,y,cset,0);
    
}

void lifegauge(BITMAP *dest,int32_t x,int32_t y, int32_t container, int32_t notlast_tile, int32_t notlast_cset, bool notlast_mod, int32_t last_tile, int32_t last_cset, bool last_mod,
               int32_t cap_tile, int32_t cap_cset, bool cap_mod, int32_t aftercap_tile, int32_t aftercap_cset, bool aftercap_mod, int32_t frames, int32_t speed, int32_t delay, bool unique_last)
{
    //these are here to bypass compiler warnings about unused arguments
    frames=frames;
    speed=speed;
    delay=delay;
    
    int32_t containers=game->get_maxlife()/game->get_hp_per_heart();
    int32_t tile=zc_oldrand()%32767, cset=zc_oldrand()%15;
    bool mod_value=(zc_oldrand()%2)!=0;
    
    if(container<containers)
    {
        tile=notlast_tile;
        cset=notlast_cset;
        mod_value=notlast_mod;
    }
    else if(container==containers)
    {
        tile=last_tile;
        cset=last_cset;
        mod_value=last_mod;
    }
    else if(container==containers+1)
    {
        tile=cap_tile;
        cset=cap_cset;
        mod_value=cap_mod;
    }
    else //if (container>containers+1)
    {
        tile=aftercap_tile;
        cset=aftercap_cset;
        mod_value=aftercap_mod;
    }
    
    if(mod_value)
    {
        if(game->get_life()>=container*game->get_hp_per_heart())
        {
            //tile=tile;                                        //full block
            if(game->get_life()==container*game->get_hp_per_heart())
            {
                if(unique_last)
                {
                    tile+=game->get_hp_per_heart()+3;
                }
            }
        }
        else
        {
            if(((container-1)*game->get_hp_per_heart())>game->get_life())
            {
                tile+=4;                                //empty block
            }
            else
            {
                tile+=4+((game->get_life()-((container-1)*game->get_hp_per_heart()))%game->get_hp_per_heart());
            }
        }
    }
    
    overtile8(dest,tile,x,y,cset,0);
    
}


void magicmeter(BITMAP *dest,int32_t x,int32_t y)
{
    if(!get_qr(qr_ENABLEMAGIC)) return;
    
    if(game->get_maxmagic()==0) return;
    
    int32_t tile;
    int32_t mmtile=wpnsbuf[iwMMeter].tile;
    int32_t mmcset=wpnsbuf[iwMMeter].csets&15;
    overtile8(dest,(mmtile*4)+2,x-8,y,mmcset,0);
    
    if(game->get_magicdrainrate()==1)
    {
        overtile8(dest,(mmtile*4)+1,x-10,y,mmcset,0);
    }
    
    for(int32_t i=0; i<game->get_maxmagic(); i+=game->get_mp_per_block())
    {
        if(game->get_magic()>=i+game->get_mp_per_block())
        {
            tile=mmtile*4;                                        //full block
        }
        else
        {
            if(i>game->get_magic())
            {
                tile=((mmtile+1)*4);                                //empty block
            }
            else
            {
                tile=((mmtile+1)*4)+((game->get_magic()-i)%game->get_mp_per_block());
            }
        }
        
        overtile8(dest,tile,x,y,mmcset,0);
        x+=8;
    }
    
    overtile8(dest,(mmtile*4)+3,x,y,mmcset,0);
    
}

