void textprintf_styled_aligned_ex(BITMAP *bmp, const FONT *f, int32_t x, int32_t y, int32_t textstyle, int32_t alignment, int32_t color, int32_t shadow, int32_t bg, const char *format, ...)
{

    char buf[512];
    va_list ap;
    ASSERT(bmp);
    ASSERT(f);
    ASSERT(format);
    
    va_start(ap, format);
    uvszprintf(buf, sizeof(buf), format, ap);
    va_end(ap);
    
    textout_styled_aligned_ex(bmp, f, buf, x, y, textstyle, alignment, color, shadow, bg);
}


void draw_textbox(BITMAP *dest, int32_t x, int32_t y, int32_t w, int32_t h, FONT *tempfont, char const* thetext, bool wword, int32_t tabsize, int32_t alignment, int32_t textstyle, int32_t color, int32_t shadowcolor, int32_t backcolor)
{
    int32_t y1 = y;
    int32_t x1;
    int32_t len;
    char s[16];
    char text[16];
    char space[16];
    char const* printed = text;
    char const* scanned = text;
    char const* oldscan = text;
    char const* ignore = NULL;
    char const* tmp;
    char const* ptmp;
    int32_t width;
    int32_t i = 0;
    int32_t noignore;
    
    usetc(s+usetc(s, '.'), 0);
    usetc(text+usetc(text, ' '), 0);
    usetc(space+usetc(space, ' '), 0);
    
    /* find the correct text */
    if(thetext != NULL)
    {
        printed = thetext;
        scanned = thetext;
    }
    
    /* loop over the entire string */
    for(;;)
    {
        width = 0;
        
        /* find the next break */
        while(ugetc(scanned))
        {
            /* check for a forced break */
            if(ugetc(scanned) == '\n')
            {
                scanned += uwidth(scanned);
                /* we are done parsing the line end */
                break;
            }
            
            /* the next character length */
            usetc(s+usetc(s, ugetc(scanned)), 0);
            len = text_length(tempfont, s);
            
            /* modify length if its a tab */
            if(ugetc(s) == '\t')
            {
                len = tabsize * text_length(tempfont, space);
            }
            
            /* check for the end of a line by excess width of next char */
            if(width+len >= w)
            {
                /* we have reached end of line do we go back to find start */
                if(wword)
                {
                    /* remember where we were */
                    oldscan = scanned;
                    noignore = FALSE;
                    
                    /* go backwards looking for start of word */
                    while(!uisspace(ugetc(scanned)))
                    {
                        /* don't wrap too far */
                        if(scanned == printed)
                        {
                            /* the whole line is filled, so stop here */
                            tmp = ptmp = scanned;
                            
                            while(ptmp != oldscan)
                            {
                                ptmp = tmp;
                                tmp += uwidth(tmp);
                            }
                            
                            scanned = ptmp;
                            noignore = TRUE;
                            break;
                        }
                        
                        /* look further backwards to wrap */
                        tmp = ptmp = printed;
                        
                        while(tmp < scanned)
                        {
                            ptmp = tmp;
                            tmp += uwidth(tmp);
                        }
                        
                        scanned = ptmp;
                    }
                    
                    /* put the space at the end of the line */
                    if(!noignore)
                    {
                        ignore = scanned;
                        scanned += uwidth(scanned);
                    }
                    else
                    {
                        ignore = NULL;
                    }
                    
                    /* check for endline at the convenient place */
                    if(ugetc(scanned) == '\n')
                    {
                        scanned += uwidth(scanned);
                    }
                }
                
                /* we are done parsing the line end */
                break;
            }
            
            /* the character can be added */
            scanned += uwidth(scanned);
            width += len;
        }
        
        /* check if we are to print it */
        if((y1+text_height(tempfont) <= (y+h)))
        {
            int32_t tempw=0;
            char const* tempprinted=printed;
            
            /* print up to the marked character */
            while(printed != scanned)
            {
                /* do special stuff for each charater */
                switch(ugetc(printed))
                {
                case '\r':
                case '\n':
                    /* don't print endlines in the text */
                    break;
                    
                    /* possibly expand the tabs */
                case '\t':
                    for(i=0; i<tabsize; i++)
                    {
                        usetc(s+usetc(s, ' '), 0);
                        tempw += text_length(tempfont, s);
                    }
                    
                    break;
                    
                    /* print a normal character */
                default:
                    if(printed != ignore)
                    {
                        usetc(s+usetc(s, ugetc(printed)), 0);
                        tempw += text_length(tempfont, s);
                    }
                }
                
                /* goto the next character */
                printed += uwidth(printed);
            }
            
            printed=tempprinted;
            
            switch(alignment)
            {
            case sstaRIGHT:
                x1=x+w-tempw;
                break;
                
            case sstaCENTER:
                x1=x+((w-tempw)/2)+1;
                break;
                
            case sstaLEFT:
            default:
                x1 = x;
                break;
            }
            
            /* print up to the marked character */
            while(printed != scanned)
            {
                /* do special stuff for each charater */
                switch(ugetc(printed))
                {
                
                case '\r':
                case '\n':
                    /* don't print endlines in the text */
                    break;
                    
                    /* possibly expand the tabs */
                case '\t':
                    for(i=0; i<tabsize; i++)
                    {
                        usetc(s+usetc(s, ' '), 0);
                        textout_styled_aligned_ex(dest, tempfont, s, x1, y1, textstyle, sstaLEFT, color, shadowcolor, backcolor);
                        x1 += text_length(tempfont, s);
                    }
                    
                    break;
                    
                    /* print a normal character */
                default:
                    if(printed != ignore)
                    {
                        usetc(s+usetc(s, ugetc(printed)), 0);
                        textout_styled_aligned_ex(dest, tempfont, s, x1, y1, textstyle, sstaLEFT, color, shadowcolor, backcolor);
                        x1 += text_length(tempfont, s);
                    }
                }
                
                /* goto the next character */
                printed += uwidth(printed);
            }
            
            /* print the line end */
            y1 += text_height(tempfont);
        }
        else if (ugetc(scanned))
        {
            scanned += uwidth(scanned);
        }
        
        printed = scanned;
        
        /* check if we are at the end of the string */
        if(!ugetc(printed))
        {
            return;
        }
    }
}



