void puttriframe(BITMAP *dest, int32_t x, int32_t y, int32_t triframecolor, int32_t numbercolor, int32_t triframetile, int32_t triframecset, int32_t triforcetile, int32_t triforcecset, bool showframe, bool showpieces, bool largepieces)
{
    if(triframetile==0)
    {
        triframetile=QMisc.colors.triframe_tile;
        triframecset=QMisc.colors.triframe_cset;
    }
    
    if(triforcetile==0)
    {
        triforcetile=QMisc.colors.triforce_tile;
        triforcecset=QMisc.colors.triforce_cset;
    }
    
    if(!largepieces)
    {
        if(showframe)
        {
            if(triframetile)
            {
                draw_block(dest,x,y,triframetile,triframecset,6,3);
            }
            else
            {
                line(dest,x+47,y,x+1,y+46,triframecolor);
                line(dest,x+48,y,x+94,y+46,triframecolor);
                hline(dest,x,y+47,x+95,triframecolor);
                line(dest,x+47,y+7,x+15,y+39,triframecolor);
                line(dest,x+48,y+7,x+80,y+39,triframecolor);
                hline(dest,x+15,y+40,x+80,triframecolor);
            }
        }
        
        if(showpieces)
        {
            for(int32_t i=0; i<8; i++)
            {
                int32_t lvl = i+1;
                
                if(get_qr(qr_4TRI) && lvl>4)
                    lvl -= 4;
                    
                if(has_item(itype_triforcepiece, lvl))
                {
                    int32_t tp=0;
                    
                    for(; tp<8; tp++)
                        if(QMisc.triforce[tp] == i+1)
                            break;
                            
                    overtile16(dest,triforcetile,x+tripiece[0][tp][0],y+tripiece[0][tp][1],triforcecset,tripiece[0][tp][2]);
                }
            }
        }
    }
    else
    {
        if(showframe)
        {
            if(triframetile)
            {
                draw_block(dest,x,y,triframetile,triframecset,7,7);
            }
            else
            {
                if(!get_qr(qr_4TRI))
                {
                    //left inside vertical
                    vline(dest,x+31,y+56,y+103,triframecolor);
                    vline(dest,x+32,y+56,y+103,triframecolor);
                    
                    //center inside vertical top
                    vline(dest,x+55,y+8   ,y+55,triframecolor);
                    vline(dest,x+56,y+8   ,y+55,triframecolor);
                    
                    //right inside vertical
                    vline(dest,x+79,y+56,y+103,triframecolor);
                    vline(dest,x+80,y+56,y+103,triframecolor);
                    
                    if(!get_qr(qr_3TRI))
                    {
                        //center inside vertical bottom
                        vline(dest,x+55,y+56,y+103,triframecolor);
                        vline(dest,x+56,y+56,y+103,triframecolor);
                    }
                }
                
                //middle inside horizontal
                hline(dest,x+32,y+55,x+79,triframecolor);
                hline(dest,x+32,y+56,x+79,triframecolor);
                
                //bottom outside horizontal
                hline(dest,x+8,y+103,x+103,triframecolor);
                
                //left outside diagonal
                line(dest,x+8,y+103,x+55,y+8,triframecolor);
                line(dest,x+9,y+102,x+55,y+9,triframecolor);
                
                //right outside diagonal
                line(dest,x+56,y+8,x+103,y+103,triframecolor);
                line(dest,x+56,y+9,x+102,y+102,triframecolor);
                
                //left inside diagonal
                line(dest,x+32,y+56,x+55,y+103,triframecolor);
                line(dest,x+32,y+57,x+54,y+102,triframecolor);
                line(dest,x+32,y+55,x+55,y+102,triframecolor);
                
                //right inside diagonal
                line(dest,x+56,y+103,x+79,y+56,triframecolor);
                line(dest,x+57,y+102,x+79,y+57,triframecolor);
                line(dest,x+56,y+102,x+79,y+55,triframecolor);
                
                for(int32_t i=0; i<8; i++)
                {
                    int32_t lvl = i+1;
                    
                    if(get_qr(qr_4TRI) && lvl>4)
                    {
                        lvl -= 4;
                    }
                    
                    int32_t tp=0;
                    
                    for(; tp<8; tp++)
                    {
                        if(QMisc.triforce[tp] == i+1)
                        {
                            break;
                        }
                    }
                    
                    if(!has_item(itype_triforcepiece, lvl))
                    {
                        textprintf_ex(dest, get_zc_font(font_pfont), x+tripiece[1][tp][0]+((tripiece[1][tp][2]&1)?8:14),y+tripiece[1][tp][1]+((tripiece[1][tp][2]&2)?9:32), numbercolor, -1, "%d", lvl);
                    }
                }
            }
        }
        
        if(showpieces)
        {
            for(int32_t i=0; i<8; i++)
            {
                int32_t lvl = i+1;
                
                if(get_qr(qr_4TRI) && lvl>4)
                {
                    lvl -= 4;
                }
                
                int32_t tp=0;
                
                for(; tp<8; tp++)
                {
                    if(QMisc.triforce[tp] == i+1)
                    {
                        break;
                    }
                }
                
                if(has_item(itype_triforcepiece, lvl))
                {
                    draw_block_flip(dest,x+tripiece[1][tp][0]-(tripiece[1][tp][2]&1?0:8),y+tripiece[1][tp][1],triforcetile,triforcecset,2,3,tripiece[1][tp][2],1,0);
                }
            }
        }
    }
}

void puttriforce(BITMAP *dest, int32_t x, int32_t y, int32_t tile, int32_t cset, int32_t w, int32_t h, int32_t flip, bool overlay, bool trans, int32_t trinum)
{
    if(tile==0)
    {
        tile=QMisc.colors.triforce_tile;
        cset=QMisc.colors.triforce_cset;
    }
    
	if(trinum <= 8)
	{
		for(int32_t i=0; i<8; i++)
		{
			int32_t lvl = i+1;
			
			if(get_qr(qr_4TRI) && lvl>4)
				lvl -= 4;
				
			if(lvl==trinum && has_item(itype_triforcepiece, lvl))
			{
				int32_t tp=0;
				
				for(; tp<8; tp++)
					if(QMisc.triforce[tp] == i+1)
						break;
						
				draw_block_flip(dest,x,y,tile,cset,w,h,flip,overlay,trans);
			}
		}
	}
	else if(has_item(itype_triforcepiece, trinum))
	{
		draw_block_flip(dest,x,y,tile,cset,w,h,flip,overlay,trans);
	}
}

