bool ArrayManager::push(int32_t val, int indx)
{
	if(_invalid) return false;
	if(!aptr)
	{
		Z_scripterrlog("Special internal array '%d' not valid for operation 'Push'\n", ptr);
		return false;
	}
	if(aptr->Size() == ZCARRAY_MAX_SIZE)
		return false;
	aptr->Push(val,indx);
	return true;
}
int32_t ArrayManager::pop(int indx)
{
	if(_invalid) return -10000;
	if(!aptr)
	{
		Z_scripterrlog("Special internal array '%d' not valid for operation 'Push'\n", ptr);
		return -10000;
	}
	if(aptr->Empty())
	{
		Z_scripterrlog("Array %d had nothing to Pop!\n",ptr);
		return -10000;
	}
	return aptr->Pop(indx);
}

std::string ArrayManager::asString(std::function<char const*(int32_t)> formatter, const size_t& limit) const
{
	if(_invalid) return "{ INVALID ARRAY }";
	std::ostringstream oss;
	oss << "{ ";
	size_t s = size();
	bool overflow = limit < s;
	if(overflow)
		s = limit;
	
	for(auto q = 0; q < s; ++q)
	{
		oss << formatter(get(q));
		if (q + 1 < s)
			oss << ", ";
	}
	if (overflow)
		oss << ", ...";
	oss << " }";
	return oss.str();
}

// Called to deallocate arrays when a script stops running
void deallocateArray(const int32_t ptrval)
{
	if(ptrval == 0) return;
	if(ptrval==0 || ptrval >= NUM_ZSCRIPT_ARRAYS)
		Z_scripterrlog("Script tried to deallocate memory at invalid address %ld\n", ptrval);
	else if(ptrval<0)
		Z_scripterrlog("Script tried to deallocate memory at object-based address %ld\n", ptrval);
	else
	{
		if(arrayOwner[ptrval].specOwned) return; //ignore this deallocation
		if(arrayOwner[ptrval].specCleared) return;
		arrayOwner[ptrval].clear();
		
		if(!localRAM[ptrval].Valid())
			Z_scripterrlog("Script tried to deallocate memory that was not allocated at address %ld\n", ptrval);
		else
			localRAM[ptrval].Clear();
	}
}

void FFScript::deallocateAllArrays(ScriptType scriptType, const int32_t UID, bool requireAlways)
{
	for(int32_t q = MIN_USER_BITMAPS; q < MAX_USER_BITMAPS; ++q)
	{
		scb.script_created_bitmaps[q].own_clear(scriptType, UID);
	}
	for(int32_t q = 0; q < MAX_USER_RNGS; ++q)
	{
		script_rngs[q].own_clear(scriptType, UID);
	}
	for (int32_t q = 0; q < MAX_USER_PALDATAS; ++q)
	{
		script_paldatas[q].own_clear(scriptType, UID);
	}
	for(int32_t q = 0; q < MAX_USER_FILES; ++q)
	{
		script_files[q].own_clear(scriptType, UID);
	}
	for(int32_t q = 0; q < MAX_USER_DIRS; ++q)
	{
		script_dirs[q].own_clear(scriptType, UID);
	}
	for(int32_t q = 0; q < MAX_USER_STACKS; ++q)
	{
		script_stacks[q].own_clear(scriptType, UID);
	}
	for(int32_t q = 0; q < max_valid_object; ++q)
	{
		script_objects[q].own_clear(scriptType, UID);
	}
	if(requireAlways && !get_qr(qr_ALWAYS_DEALLOCATE_ARRAYS))
	{
		//Keep 2.50.2 behavior if QR unchecked.
		switch(scriptType)
		{
			case ScriptType::FFC:
			case ScriptType::Item:
			case ScriptType::Global:
				return;
		}
	}
	//Z_eventlog("Attempting array deallocation from %s UID %d\n", script_types[scriptType], UID);
	for(int32_t i = 1; i < NUM_ZSCRIPT_ARRAYS; i++)
	{
		if(arrayOwner[i].scriptType == scriptType && arrayOwner[i].ownerUID==UID)
		{
			arrayOwner[i].specOwned = false;
			deallocateArray(i);
			//Z_eventlog("Deallocated array %d from %s UID %d\n", i, script_types[scriptType], UID);
		}
	}
}

void FFScript::deallocateAllArrays()
{
	for(int32_t q = MIN_USER_BITMAPS; q < MAX_USER_BITMAPS; ++q)
	{
		scb.script_created_bitmaps[q].own_clear_any();
	}
	for(int32_t q = 0; q < MAX_USER_RNGS; ++q)
	{
		script_rngs[q].own_clear_any();
	}
	for (int32_t q = 0; q < MAX_USER_PALDATAS; ++q)
	{
		script_paldatas[q].own_clear_any();
	}
	for(int32_t q = 0; q < MAX_USER_FILES; ++q)
	{
		script_files[q].own_clear_any();
	}
	for(int32_t q = 0; q < MAX_USER_DIRS; ++q)
	{
		script_dirs[q].own_clear_any();
	}
	for(int32_t q = 0; q < MAX_USER_STACKS; ++q)
	{
		script_stacks[q].own_clear_any();
	}
	for(int32_t q = 0; q < max_valid_object; ++q)
	{
		script_objects[q].own_clear_any();
	}
	//No QR check here- always deallocate on quest exit.
	for(int32_t i = 1; i < NUM_ZSCRIPT_ARRAYS; i++)
	{
		if(localRAM[i].Valid())
		{
			arrayOwner[i].specOwned = false;
			//Z_eventlog("Deallocated array %d from %s UID %d\n", i, script_types[arrayOwner[i].scriptType], arrayOwner[i].ownerUID);
			deallocateArray(i);
		}
	}
}

