void do_selectweapon(bool v, int32_t btn)
{
	switch(btn)
	{
		case 1:
			if(!get_qr(qr_SELECTAWPN))
				return;
			break;
		case 2:
			if(!get_qr(qr_SET_XBUTTON_ITEMS))
				return;
			break;
		case 3:
			if(!get_qr(qr_SET_YBUTTON_ITEMS))
				return;
			break;
	}
		
	byte dir=(byte)(SH::get_arg(sarg1, v)/10000);
	
	// Selection directions don't match the normal ones...
	switch(dir)
	{
	case 0:
		dir=SEL_UP;
		break;
		
	case 1:
		dir=SEL_DOWN;
		break;
		
	case 2:
		dir=SEL_LEFT;
		break;
		
	case 3:
		dir=SEL_RIGHT;
		break;
		
	default:
		return;
	}
	
	switch(btn)
	{
		case 0:
			selectNextBWpn(dir);
			break;
		case 1:
			selectNextAWpn(dir);
			break;
		case 2:
			selectNextXWpn(dir);
			break;
		case 3:
			selectNextYWpn(dir);
			break;
	}
}

