void do_clearsprites(const bool v)
{
	int32_t spritelist = SH::get_arg(sarg1, v) / 10000;
	
	if(BC::checkBounds(spritelist, 0, 5, "Screen->ClearSprites") != SH::_NoError)
		return;
		
	switch(spritelist)
	{
		case 0:
			guys.clear();
			break;
			
		case 1:
			items.clear();
			break;
			
		case 2:
			Ewpns.clear();
			break;
			
		case 3:
			Lwpns.clear();
			Hero.reset_hookshot();
			break;
			
		case 4:
			decorations.clear();
			break;
			
		case 5:
			particles.clear();
			break;
	}
}

