void doWarpEffect(int32_t warpEffect, bool out)
{
	switch(warpEffect)
	{
		case warpEffectZap:
			if(out) zapout();
			else zapin();
			break;
		case warpEffectWave:
			if(out) wavyout(false);
			else wavyin();
			break;
		case warpEffectInstant:
			if(out) blackscr(30,true);
			break;
		case warpEffectMozaic:
			//!TODO Unimplemented
			break;
		case warpEffectOpen:
			if(out) closescreen();
			else openscreen();
			break;
	}
}

