	int32_t ID = (ri->d[rINDEX]/10000);  \
	int32_t indx =  vbound((ri->d[rINDEX2]/10000),0,indexbound);  \
	if(ID < 1 || ID > (MAXGUYS-1)) \
		return; \
	else \
		guysbuf[ID].member[indx] = vbound(val,0,bound); \
