int32_t FFScript::getEnemyByScriptUID(int32_t sUID)
{
	
	for(word i = 0; i < guys.Count(); i++)
		{
			enemy *w = (enemy*)guys.spr(i);
			if ( w ->script_UID == sUID ) return i;
		}
	return -1;
}

