bool command_is_wait(int command)
{
	switch (command)
	{
	case WAITFRAME:
	case WAITDRAW:
	case WAITTO:
	case WAITEVENT:
	case WAITFRAMESR:
		return true;
	}
	return false;
}

bool command_uses_comparison_result(int command)
{
	switch (command)
	{
	case GOTOTRUE:
	case GOTOFALSE:
	case GOTOMORE:
	case GOTOLESS:
	case SETTRUE:
	case SETTRUEI:
	case SETFALSE:
	case SETFALSEI:
	case SETMOREI:
	case SETLESSI:
	case SETMORE:
	case SETLESS:
		return true;
	}
	return false;
}

